import { Component } from '@angular/core';
import { SidebarStateService } from '../../services/sidebar-state.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent {
  isSidebarOpen = false;
  isReturning = false;

  constructor(private sidebarStateService: SidebarStateService) {}

  toggleSidebar(event: Event) {
    this.sidebarStateService.toggle();
    event.stopPropagation();
    if (!this.isSidebarOpen) {
      this.isReturning = true;
      setTimeout(() => {
        this.isReturning = false;
      }, 250);
    }
  }

  ngOnInit() {
    this.sidebarStateService.isOpen$.subscribe((isOpen) => {
      this.isSidebarOpen = isOpen;
    });
  }
}
<div class="header_container">
  <mat-drawer-container class="sidenav_container">
    <button
      type="button"
      mat-button
      (click)="toggleSidebar($event)"
      class="sidebar_button"
    >
      <mat-icon
        aria-hidden="false"
        aria-label="sidebar button"
        fontIcon="reorder"
      ></mat-icon>
    </button>
  </mat-drawer-container>
  <div class="absolute boat">
    <ul class="no-bullet">
      <ul class="no-bullet fume">
        <li class="fume4"></li>
        <li class="fume3"></li>
        <li class="fume2"></li>
        <li class="fume1"></li>
      </ul>
      <li class="smokestack"></li>
      <li class="white-body">
        <ul class="windows inline-list">
          <li class="circle"></li>
          <li class="circle"></li>
          <li class="circle"></li>
        </ul>
      </li>
      <li class="boat-body"></li>
    </ul>
  </div>
  <img src="assets/logos/logo.svg" />
</div>
