import { Component, HostListener } from '@angular/core';
import { SidebarStateService } from '../../services/sidebar-state.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent {
  isSidebarOpen = false;
  isReturning = false;

  headerHeight: string = '25vh';
  isFixed: boolean = true;
  isGhostVisible: boolean = false;

  constructor(
    private sidebarStateService: SidebarStateService,
    private router: Router
  ) {}

  toggleSidebar(event: Event) {
    this.sidebarStateService.toggle();
    event.stopPropagation();
    if (!this.isSidebarOpen) {
      this.isReturning = true;
      setTimeout(() => {
        this.isReturning = false;
      }, 250);
    }
  }

  ngOnInit() {
    this.sidebarStateService.isOpen$.subscribe((isOpen) => {
      this.isSidebarOpen = isOpen;
    });
  }

  redirectToIndex() {
    this.router.navigate(['']);
  }

  @HostListener('window:scroll', ['$event'])
  onWindowScroll(event: Event): void {
    let scrollOffset =
      window.pageYOffset ||
      document.documentElement.scrollTop ||
      document.body.scrollTop ||
      0;
    if (scrollOffset > 0 && scrollOffset <= 200) {
      let newHeight = 25 - (scrollOffset / 200) * 10;
      this.headerHeight = `${newHeight}vh`;
      this.isFixed = true;
    } else if (scrollOffset > 200) {
      this.headerHeight = '15vh';
      this.isFixed = false;
      this.isGhostVisible = true;
    } else {
      this.headerHeight = '25vh';
      this.isFixed = true;
      this.isGhostVisible = false;
    }
  }
}
<div
  *ngIf="isGhostVisible"
  class="header_ghost"
  [style.height]="headerHeight"
></div>
<div
  class="header_container"
  [style.height]="headerHeight"
  [class.fixed]="isFixed"
>
  <mat-drawer-container class="sidenav_button_container">
    <button
      type="button"
      (click)="toggleSidebar($event)"
      class="sidebar_button"
      matTooltip="Menu"
      [matTooltipPosition]="'right'"
    >
      <mat-icon
        aria-hidden="false"
        aria-label="sidebar button"
        fontIcon="reorder"
      ></mat-icon>
    </button>
  </mat-drawer-container>
  <img
    class="logo"
    src="assets/logos/logo.svg"
    alt="logo crafted web"
    (click)="redirectToIndex()"
  />
</div>
