import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import {
  trigger,
  state,
  style,
  animate,
  transition,
} from '@angular/animations';

interface IProject {
  id: string;
  name: string;
  image: string;
  title: string;
  technos: string;
  description: string;
  link: string;
  repo_link: string;
}

@Component({
  selector: 'app-realisations',
  templateUrl: './realisations.component.html',
  styleUrls: ['./realisations.component.scss'],
  animations: [
    trigger('slideFromRight', [
      state('in', style({ transform: 'translateX(0)' })),
      transition('void => *', [
        style({ transform: 'translateX(100%)' }),
        animate(250),
      ]),
      transition('* => void', [
        animate(250, style({ transform: 'translateX(-100%)' })),
      ]),
    ]),
  ],
})
export class RealisationsComponent {
  projectsDatasOc: IProject[];

  constructor(private http: HttpClient) {
    this.projectsDatasOc = [];
  }

  ngOnInit() {
    this.http
      .get<IProject[]>('assets/datas/datassites_oc.json')
      .subscribe((data) => {
        this.projectsDatasOc = data.reverse();
      });
  }
}
<div class="realisations_container">
  <h2>Réalisations OpenClassorooms</h2>
  <div class="cards_container" @slideFromRight>
    <mat-card class="card_container" *ngFor="let project of projectsDatasOc">
      <mat-card-header>
        <mat-card-title class="project_title">{{
          project.name
        }}</mat-card-title>
        <mat-card-subtitle class="project_subtitle">{{
          project.title
        }}</mat-card-subtitle>
      </mat-card-header>
      <mat-card-content class="project_technos">
        <p>
          {{ project.technos }}
        </p>
      </mat-card-content>
      <mat-card-content class="project_description">
        <p>
          {{ project.description }}
        </p>
      </mat-card-content>
      <mat-card-actions class="links_container">
        <a class="links" mat-raised-button href="{{ project.link }}">Site</a>
        <a class="links" mat-raised-button href="{{ project.repo_link }}"
          >Repo</a
        >
      </mat-card-actions>
    </mat-card>
  </div>
</div>
