import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface IProject {
  id: string;
  name: string;
  image: string;
  title: string;
  technos: string;
  description: string;
  link: string;
  'repo link': string;
}

@Component({
  selector: 'app-realisations',
  templateUrl: './realisations.component.html',
  styleUrls: ['./realisations.component.scss'],
})
export class RealisationsComponent {
  projectsDatas: IProject[];

  constructor(private http: HttpClient) {
    this.projectsDatas = [];
  }

  ngOnInit() {
    this.http
      .get<IProject[]>('assets/datas/datassites.json')
      .subscribe((data) => {
        console.log(data);
        this.projectsDatas = data;
      });
  }
}
<div class="cards_container">
  <mat-card class="card_container" *ngFor="let project of projectsDatas">
    <mat-card-header>
      <mat-card-title>{{ project.name }}</mat-card-title>
      <mat-card-subtitle class="project_subtitle">{{
        project.title
      }}</mat-card-subtitle>
    </mat-card-header>
    <img
      class="project_pic"
      mat-card-image
      src="assets/logos_projects/{{ project.image }}.png"
      alt="logo du projet {{ project.name }}"
    />
    <mat-card-content class="project_technos">
      <p>
        {{ project.technos }}
      </p>
    </mat-card-content>
    <mat-card-content class="project_description">
      <p>
        {{ project.description }}
      </p>
    </mat-card-content>
    <mat-card-actions>
      <button mat-button>LIKE</button>
      <button mat-button>SHARE</button>
    </mat-card-actions>
  </mat-card>
</div>
