import { Component, HostBinding } from '@angular/core';
import { SidebarStateService } from '../../services/sidebar-state.service';
import {
  trigger,
  state,
  style,
  animate,
  transition,
} from '@angular/animations';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.scss'],
  animations: [
    trigger('linkHover', [
      state('inactive', style({ marginLeft: '0' })),
      state('active', style({ marginLeft: '20px' })),
      transition('inactive <=> active', animate('0.25s ease-out')),
    ]),
  ],
})
export class SidebarComponent {
  showFiller = false;
  isOpen$: Observable<boolean>;
  linkHoverState = 'inactive'; // Ajout de la variable

  constructor(private sidebarStateService: SidebarStateService) {
    this.isOpen$ = this.sidebarStateService.isOpen$;
  }

  toggle() {
    this.sidebarStateService.toggle();
  }

  onMouseOver() {
    this.linkHoverState = 'active';
  }

  onMouseLeave() {
    this.linkHoverState = 'inactive';
  }
}
<mat-drawer #drawer class="sidenav" mode="side" [opened]="isOpen$ | async">
  <div class="sidebar_container">
    <div class="pic_container">
      <img class="pic_profile" src="../../../assets/pics/pic_profile.png" />
    </div>
    <div class="sidebar_links_container">
      <a class="sidebar_links" routerLink="realisations">Réalisations</a>
      <a class="sidebar_links" routerLink="professional"
        >Parcours Professionnel</a
      >
      <a class="sidebar_links" routerLink="scholar">Parcours Scolaire</a>
      <a class="sidebar_links" routerLink="aboutme">A propos de moi</a>
    </div>

    <p>Lorem ipsum</p>
  </div>

  <!-- <p *ngIf="showFiller">Lorem, ipsum dolor sit amet consectetur.</p>
    <button (click)="showFiller = !showFiller" mat-raised-button>
      Toggle extra text
    </button> -->
</mat-drawer>
