import { Component } from '@angular/core';
import { Observable } from 'rxjs';
import { SidebarStateService } from '../../services/sidebar-state.service';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.scss'],
})
export class SidebarComponent {
  showFiller = false;
  isOpen$: Observable<boolean>; // Déclaration de la variable

  constructor(private sidebarStateService: SidebarStateService) {
    this.isOpen$ = this.sidebarStateService.isOpen$; // Assignation de la valeur de l'Observable
  }

  toggle() {
    this.sidebarStateService.toggle();
  }
}
<mat-drawer #drawer class="sidenav" mode="side" [opened]="isOpen$ | async">
  <div class="sidebar_container">
    <div class="pic_container">
      <img class="pic_profile" src="../../../assets/pics/pic_profile.png" />
    </div>
    <div class="sidebar_links_container">
      <ng-container *ngFor="let link of linkName">
        <div
          class="sidebar_link_container"
          (click)="onLinkClick(link)"
          (mouseover)="onMouseOver(link)"
          (mouseleave)="onMouseLeave()"
          [@linkContainerHover]="
            hoveredLinkContainer === link.link ? 'active' : 'inactive'
          "
        >
          <p class="sidebar_links">{{ link.value }}</p>
        </div>
      </ng-container>
    </div>
  </div>
  <p class="sidebar_copyright">
    Lorem Ipsum©, <br />
    None Rights Reserved
  </p>
</mat-drawer>
