import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface IProject {
  id: string;
  name: string;
  image: string;
  title: string;
  technos: string;
  description: string;
  link: string;
  'repo link': string;
}

@Component({
  selector: 'app-realisations',
  templateUrl: './realisations.component.html',
  styleUrls: ['./realisations.component.scss'],
})
export class RealisationsComponent {
  projectsDatas: IProject[];

  constructor(private http: HttpClient) {
    this.projectsDatas = [];
  }

  ngOnInit() {
    this.http
      .get<IProject[]>('assets/datas/datassites.json')
      .subscribe((data) => {
        console.log(data);
        this.projectsDatas = data;
      });
  }
}
<div class="cards_container">
  <mat-card class="example-card" *ngFor="let project of projectsDatas">
    <mat-card-header>
      <div mat-card-avatar class="example-header-image"></div>
      <mat-card-title>{{ project.name }}</mat-card-title>
      <mat-card-subtitle class="project_subtitle">{{
        project.title
      }}</mat-card-subtitle>
    </mat-card-header>
    <img
      mat-card-image
      src="https://material.angular.io/assets/img/examples/shiba2.jpg"
      alt="Photo of a Shiba Inu"
    />
    <mat-card-content>
      <p>
        The Shiba Inu is the smallest of the six original and distinct spitz
        breeds of dog from Japan. A small, agile dog that copes very well with
        mountainous terrain, the Shiba Inu was originally bred for hunting.
      </p>
    </mat-card-content>
    <mat-card-actions>
      <button mat-button>LIKE</button>
      <button mat-button>SHARE</button>
    </mat-card-actions>
  </mat-card>
</div>
