import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-realisations',
  templateUrl: './realisations.component.html',
  styleUrls: ['./realisations.component.scss'],
})
export class RealisationsComponent {
  projectsDatas: string[];

  constructor(private http: HttpClient) {
    this.projectsDatas = [];
  }

  ngOnInit() {
    this.http
      .get<string[]>('assets/datas/datassites.json')
      .subscribe((data) => {
        console.log(data);
        this.projectsDatas = data;
      });
  }
}
<div class="cards_container">
  <mat-card class="example-card" *ngFor="let project of projectsDatas">
    <mat-card-header>
      <mat-card-title-group>
        <mat-card-title>Shiba Inu</mat-card-title>
        <mat-card-subtitle>Large</mat-card-subtitle>
        <img
          mat-card-lg-image
          src="https://material.angular.io/assets/img/examples/shiba2.jpg"
        />
      </mat-card-title-group>
    </mat-card-header>
    <mat-card-content> test </mat-card-content>
  </mat-card>
</div>
