import { Component, OnInit, OnDestroy } from '@angular/core';
import { interval, Subscription } from 'rxjs';

@Component({
  selector: 'app-active-users',
  templateUrl: './active-users.component.html',
  styleUrls: ['./active-users.component.scss'],
})
export class ActiveUsersComponent implements OnInit, OnDestroy {
  publicUser: number;
  increaseSubscription: Subscription | null = null;
  decreaseSubscription: Subscription | null = null;
  range = 9000;

  constructor() {
    this.publicUser = Math.floor(Math.random() * 9420658);
  }

  ngOnInit(): void {
    this.increaseSubscription = interval(6000).subscribe(() =>
      this.increasePublic()
    );
    this.decreaseSubscription = interval(3000).subscribe(() =>
      this.decreasePublic()
    );
  }

  ngOnDestroy(): void {
    this.increaseSubscription!.unsubscribe();
    this.decreaseSubscription!.unsubscribe();
  }

  increasePublic(): void {
    const randomNumber = Math.floor(Math.random() * this.range);
    this.publicUser += randomNumber;
  }

  decreasePublic(): void {
    const randomNumber = Math.floor(Math.random() * this.range);
    this.publicUser -= randomNumber;
  }
}
<div class="user_block">
  <p>Utilisateurs sur le site :</p>
  <p>{{ publicUser }} utilisateurs</p>
</div>
