import { Component } from '@angular/core';
import { SidebarStateService } from '../../services/sidebar-state.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent {
  isSidebarOpen = false;
  isReturning = false;

  constructor(
    private sidebarStateService: SidebarStateService,
    private router: Router
  ) {}

  toggleSidebar(event: Event) {
    this.sidebarStateService.toggle();
    event.stopPropagation();
    if (!this.isSidebarOpen) {
      this.isReturning = true;
      setTimeout(() => {
        this.isReturning = false;
      }, 250);
    }
  }

  ngOnInit() {
    this.sidebarStateService.isOpen$.subscribe((isOpen) => {
      this.isSidebarOpen = isOpen;
    });
  }

  redirectToIndex() {
    this.router.navigate(['']);
  }
}
<div class="header_container" [style.height]="headerHeight">
  <mat-drawer-container class="sidenav_button_container">
    <button
      type="button"
      (click)="toggleSidebar($event)"
      class="sidebar_button"
      matTooltip="Menu"
      [matTooltipPosition]="'right'"
    >
      <mat-icon
        aria-hidden="false"
        aria-label="sidebar button"
        fontIcon="reorder"
      ></mat-icon>
    </button>
  </mat-drawer-container>
  <img
    class="logo"
    src="assets/logos/logo.svg"
    alt="logo crafted web"
    (click)="redirectToIndex()"
  />
</div>
