import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent {
  sidebarOpen = false;

  toggleSidebar() {
    this.sidebarOpen = !this.sidebarOpen;
  }
}
<div class="header_container">
  <button type="button" mat-button (click)="toggleSidebar()">
    Toggle Sidebar
  </button>
</div>
<app-sidebar *ngIf="sidebarOpen" [showFiller]="showFiller"></app-sidebar>
