import { Component, Renderer2, AfterViewInit, ViewChild } from '@angular/core';
import { MatDrawer } from '@angular/material/sidenav';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent implements AfterViewInit {
  @ViewChild('drawer') drawer!: MatDrawer;

  constructor(private renderer: Renderer2) {}

  ngAfterViewInit() {
    // Appeler le renderer pour s'assurer que le composant est initialisé avant d'accéder au drawer
    this.renderer.setProperty(this.drawer, 'toggle', () =>
      this.drawer.toggle()
    );
  }
}
<div class="header_container">
  <mat-drawer-container class="sidenav_container" autosize>
    <mat-drawer #drawer class="example-sidenav" mode="side">
      <p>Auto-resizing sidenav</p>
      <p *ngIf="showFiller">Lorem, ipsum dolor sit amet consectetur.</p>
      <button (click)="showFiller = !showFiller" mat-raised-button>
        Toggle extra text
      </button>
    </mat-drawer>

    <div class="example-sidenav-content">
      <button type="button" mat-button (click)="drawer.toggle()">
        Toggle sidenav
      </button>
    </div>
  </mat-drawer-container>
</div>
