import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-bubbles',
  templateUrl: './bubbles.component.html',
  styleUrls: ['./bubbles.component.scss'],
})
export class BubblesComponent implements OnInit {
  bubbles: any[] = [];
  numberOfBubbles: number = 10;

  ngOnInit(): void {
    for (let i = 0; i < this.numberOfBubbles; i++) {
      const size = Math.random() * 50;
      const position = Math.random() * 100;
      const speed = Math.random() * 5;
      this.bubbles.push({ size, position, speed });
    }
  }
}
<div class="bubbles_container">
  <div
    *ngFor="let bubble of bubbles"
    [ngStyle]="{
      width: bubble.size + 'px',
      height: bubble.size + 'px',
      left: bubble.position + '%',
      'animation-duration': bubble.speed + 's'
    }"
    class="bubble"
  ></div>
</div>
